#endif

#define RECV_QUEUE_MEM_SIZE 512

TX_QUEUE *recv_queue;
uint32_t recv_queue_mem[RECV_QUEUE_MEM_SIZE];



static void rx_queue_task(ULONG param);
TX_THREAD* rx_queue_thread_handle; 
