static int cli_cmd_echo(int args, char *argv[]){
	int opt;
	char *file = NULL;
	char *str = argv[1];
	bool append = false;
	optind = 2;

	while((opt=getopt(args, argv, ":f:a")) != -1){
    	switch(opt) {
    		case 'f':
    			file = strdup(optarg);
    			break;
    		case 'a':
    			append = true;
    			break;
    		default:
    			goto usage;
			break;
    	}
    }

    if(file != NULL && str != NULL){
    	if(append){
    		FILE *fp = fopen(file,"a");
    		if(fp){
    			fwrite(str,1,strlen(str),fp);
    			fclose(fp);
    		}
    	}else{
      		FILE *fp = fopen(file,"w");
    		if(fp){
    			fwrite(str,1,strlen(str),fp);
    			fclose(fp);
    		}  		
    	}

    	free(file);

    	return 0;
    }

usage:
	puts("\r\necho\r\n"
	"Write or append to a file. Default is write.\r\n\r\n"
	"Syntax:\r\necho STRING [options]\r\n\r\n"
	"Options:\r\n\r\n"
	"\t-f\r\n\tFile to write or append to.\r\n"
	"\t-a\r\n\tAppend to a file.\r\n");

	return 0;

}


